 defined( '_JEXEC' ) or die(); require_once( JPATH_COMPONENT . '/helpers/rights.php' ); require_once( JPATH_COMPONENT . '/helpers/config.php' ); require_once( JPATH_COMPONENT . '/helpers/message.php' ); class AimySitemapViewNotify extends JViewLegacy { protected $allow_notify = false; protected $allow_config = false; protected $ping_cfg = null; protected $has_sitemap_xml = false; protected $show_sitemap_field_hint = false; public function display( $tpl = null ) { $errors = $this->get( 'Errors' ); if ( is_array( $errors ) && count( $errors ) ) { throw new Exception( implode( "\n", $errors ), 500 ); return false; } $rights = AimySitemapRightsHelper::getRights(); $this->allow_notify = $rights->get( 'aimysitemap.notify' ); $this->allow_config = $rights->get( 'core.admin' ); $this->set_has_sitemap_xml(); if ( ! $this->has_sitemap_xml ) { $msg = new AimySitemapMessageHelper(); $msg->error( JText::_( 'AIMY_SM_WARN_XML_SITEMAP_MISSING' ) . ' &rarr; ' . sprintf( '<a href="%s">%s &amp; %s</a>', JRoute::_( 'index.php?option=com_aimysitemap&view=urls' ), JText::_( 'AIMY_SM_LINK_MANAGE' ), JText::_( 'AIMY_SM_WRITE_LBL' ) ) ); } if ( JUri::root( true ) == '' ) { $path = JPATH_ROOT . '/robots.txt'; $hint = false; if ( file_exists( $path ) ) { $s = file_get_contents( $path ); if ( $s !== false ) { $this->show_sitemap_field_hint = ( empty( $s ) or ! preg_match( '#^Sitemap:#mi', $s ) ); } } else { $this->show_sitemap_field_hint = true; } } if ( $this->allow_notify ) { JFactory::getDocument()->addScript( JUri::root( true ) . '/media/com_aimysitemap/ping.js' . '?r=33.1' ); $this->set_ping_cfg(); if ( $this->ping_cfg[ 'ses' ][ 'indexnow' ] ) { require_once( JPATH_COMPONENT. '/IndexNow.php' ); try { AimySitemapIndexNow::load(); } catch ( Exception $e ) { $msg = new AimySitemapMessageHelper(); $msg->error( $e->getMessage() ); } } } else { $msg = new AimySitemapMessageHelper(); $msg->error( JText::_( 'JLIB_APPLICATION_ERROR_ACCESS_FORBIDDEN' ) ); } $this->addToolbar(); parent::display( $tpl ); } private function set_ping_cfg() { $cfg = new AimySitemapConfigHelper(); $this->ping_cfg = array( 'ses' => array( 'indexnow' => $cfg->get( 'notify_indexnow' ) ), 'url' => JUri::root() . $cfg->get( 'xml_path' ) ); } private function set_has_sitemap_xml() { $cfg = new AimySitemapConfigHelper(); $this->has_sitemap_xml = is_file( JPATH_ROOT . '/' . $cfg->get( 'xml_path' ) ); } protected function addToolbar() { $bar = JToolBar::getInstance( 'toolbar' ); JToolBarHelper::title( JText::_( 'AIMY_SM_NOTIFY' ), '' ); if ( $this->allow_notify ) { JToolBarHelper::custom( 'notify.ping', 'tree-2', JText::_( 'AIMY_SM_NOTIFY_DSC' ), JText::_( 'AIMY_SM_NOTIFY_LBL' ), false ); } if ( $this->allow_config ) { JToolBarHelper::preferences( 'com_aimysitemap' ); } } } 
